import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const QRView = () => {
  const { trackingId } = useParams(); // Get tracking ID from URL
  const [assetData, setAssetData] = useState(null); // State to hold asset data
  const [loading, setLoading] = useState(true); // State to manage loading state
  const [error, setError] = useState(null); // State to manage error

  useEffect(() => {
    // Make the GET request to fetch asset data by tracking ID
    axios.get(`http://localhost:8000//${trackingId}`)
      .then(response => {
        setAssetData(response.data); // Store the response data in the state
        setLoading(false); // Set loading to false once the data is fetched
      })
      .catch(error => {
        console.error("Error fetching asset details:", error);
        setError("Failed to fetch asset details. Please try again later.");
        setLoading(false); // Set loading to false even if there's an error
      });
  }, [trackingId]);

  if (loading) {
    return <p>Loading...</p>; // Display a loading message while fetching data
  }

  if (error) {
    return <p>Error: {error}</p>; // Display error message if there's an error
  }

  return (
    <div>
      <h2>Asset Details</h2>
      <p>Tracking ID: {trackingId}</p>
      {assetData ? (
        <div>
          <h3>Asset Information</h3>
          <p>Asset Name: {assetData.name}</p>
          <p>Serial Number: {assetData.serialNumber}</p>
          <p>Category: {assetData.mainCategory}</p> {/* Adjust field names */}
          {/* Add more fields based on your asset data structure */}
        </div>
      ) : (
        <p>No asset found with this tracking ID.</p>
      )}
    </div>
  );
};

export default QRView;
