import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const QRView = () => {
  const { trackingId } = useParams(); // Get tracking ID from URL
  const [assetData, setAssetData] = useState(null); // State to hold asset data
  const [loading, setLoading] = useState(true); // State to manage loading state
  const [error, setError] = useState(null); // State to manage error

  useEffect(() => {
    const fetchAssetData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/assets/${trackingId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch asset data');
        }
        const data = await response.json();
        setAssetData(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
  
    fetchAssetData();
  }, [trackingId]);
  

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div>
      <h2>Asset Details</h2>
      <p>Tracking ID: {trackingId}</p>
      {assetData ? (
        <div>
          <h3>Asset Information</h3>
          <p>Asset Name: {assetData.name}</p>
          <p>Serial Number: {assetData.serialNumber}</p>
          <p>Category: {assetData.mainCategory}</p> {/* Adjust field names */}
          {/* Add more fields based on your asset data structure */}
        </div>
      ) : (
        <p>No asset found with this tracking ID.</p>
      )}
    </div>
  );
};

export default QRView;
