import React, { useState, useEffect } from "react";
import axios from "axios";
import "./report.css";

const Report = () => {
  const [company, setCompany] = useState("");
  const [department, setDepartment] = useState("");
  const [mainCategory, setMainCategory] = useState("");
  const [type, setType] = useState("");
  const [computerComponents, setComputerComponents] = useState("");
  const [companies, setCompanies] = useState([]); // Dynamic company list
  const [assetRegisterDetails, setAssetRegisterDetails] = useState([]);

  const mainCategories = [
    "Electronic items",
    "Laundry & Linen",
    "Housekeeping Supplies",
    "Electrical items",
    "Security & Safety",
    "Furniture",
    "Outdoor & Garden Equipment",
    "Stationery",
  ];
  
  const departments = ["ICT", "HR", "Kitchen", "Front Office", "Store", "Account", "Audit"];
  const types = ["Computer", "Printer", "Scanner"];

  useEffect(() => {
    fetchAssets();
  }, []);

  const fetchAssets = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/AssetRegisterDetails/getAssetDetails");
      setAssetRegisterDetails(response.data);

      // Extract unique companies from asset data
      const uniqueCompanies = [...new Set(response.data.map(asset => asset.company))];
      setCompanies(uniqueCompanies);
    } catch (error) {
      console.error("Error fetching asset details:", error);
    }
  };

  const handleComponentChange = (e) => {
    setComputerComponents(e.target.value);
  };

  return (
    <div>
      {/* Company Selection */}
      <select value={company} onChange={(e) => setCompany(e.target.value)}>
        <option value="">Select Company</option>
        {companies.map((com, index) => (
          <option key={index} value={com}>
            {com}
          </option>
        ))}
      </select>

      {/* Department Selection */}
      <select value={department} onChange={(e) => setDepartment(e.target.value)}>
        <option value="">Select Department</option>
        {departments.map((dep, index) => (
          <option key={index} value={dep}>
            {dep}
          </option>
        ))}
      </select>

      {/* Category Selection */}
      <select value={mainCategory} onChange={(e) => setMainCategory(e.target.value)}>
        <option value="">Select Category</option>
        {mainCategories.map((cat, index) => (
          <option key={index} value={cat}>
            {cat}
          </option>
        ))}
      </select>

      {/* Type Selection */}
      {mainCategory && (
        <div className="col-md-6">
          <select value={type} onChange={(e) => setType(e.target.value)}>
            <option value="">Select Type</option>
            {types.map((t, index) => (
              <option key={index} value={t}>
                {t}
              </option>
            ))}
          </select>

          {/* Computer Components Selection */}
          {type === "Computer" && (
            <div className="computer-options">
              <label className="l">
                <input
                  type="radio"
                  name="computerComponent"
                  value="fullSet"
                  onChange={handleComponentChange}
                  checked={computerComponents === "fullSet"}
                /> 
                Full Set
              </label>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Report;
