import React from "react";
import { useLocation } from "react-router-dom";
import "./transfer.css";

const TransferForm = () => {
  const location = useLocation();
  const asset = location.state?.asset; // Get asset details from navigation state

const user = JSON.parse(localStorage.getItem("currentUser"));

  const [name, setName] = useState(user?.username || "");

  return (
    <div className="transfer-container">
      <h2 className="trnshead">Transfer Asset</h2>
      <form className="transfer-form">
        <div className="form-group">
          <label className="l1">Transfer Name:</label>
          <input className="in1" value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter your Name"
            readOnly={Boolean(user)} />
        </div>

        <div className="form-group">
          <label className="l1">Serial Number:</label>
          <input className="in1" type="text" value={asset?.serialNumber || ""} readOnly />
        </div>

        <div className="form-group">
          <label className="l1">Tracking ID:</label>
          <input className="in1" type="text" value={asset?.trackingId || ""} readOnly />
        </div>

        <div className="form-group">
          <label className="l1">Transfer To:</label>
          <input className="in1" type="text" placeholder="Enter recipient" />
        </div>

        <button type="submit" className="transfer-button">Transfer</button>
      </form>
    </div>
  );
};

export default TransferForm;
