import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const AssetDetails = () => {
  const { trackingId } = useParams(); // Get tracking ID from URL
  const [assetData, setAssetData] = useState(null); // State to hold asset data
  const [loading, setLoading] = useState(true); // State to manage loading state
  const [error, setError] = useState(null); // State to manage error

  useEffect(() => {
    // Fetch asset details from the backend
    const fetchAssetData = async () => {
      try {
        const response = await fetch(`/api/${trackingId}`); // Adjust API endpoint
        if (!response.ok) {
          throw new Error('Failed to fetch asset data');
        }
        const data = await response.json();
        setAssetData(data); // Store the asset data
      } catch (err) {
        setError(err.message); // Handle error
      } finally {
        setLoading(false); // Stop loading once the request is complete
      }
    };

    fetchAssetData();
  }, [trackingId]); // Re-run effect if trackingId changes

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div>
      <h2>Asset Details</h2>
      <p>Tracking ID: {trackingId}</p>
      {assetData ? (
        <div>
          <h3>Asset Information</h3>
          <p>Asset Name: {assetData.name}</p>
          <p>Serial Number: {assetData.serialNumber}</p>
          <p>Category: {assetData.category}</p>
          {/* Add more fields based on your asset data structure */}
        </div>
      ) : (
        <p>No asset found with this tracking ID.</p>
      )}
    </div>
  );
};

export default AssetDetails;
