import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './login.css';

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [showError, setShowError] = useState(false);
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [otp, setOtp] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [step, setStep] = useState(1);
  
  const navigate = useNavigate();

  const validateForm = (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError("All fields are required.");
      setShowError(true);
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm(e)) return;
  
    try {
      const response = await axios.post('http://localhost:8000/api/users/login', { username, password });
      const user = response.data;
      setUsername("");
      setPassword("");
      setShowError(false);
  
      if (user.isBlocked) {
        setError("Your account is blocked. Please contact the administrator.");
        setShowError(true);
        return;
      }
  
      localStorage.setItem('currentUser', JSON.stringify({ username: user.name, role: user.selectedOption }));
  
      if (user.selectedOption === "Admin") navigate('/AdminDashboardPage');
      else if (user.selectedOption === "CompanyAdmin") navigate('/CompanyDashBord');
      else if (user.selectedOption === "DepartmentAdmin") navigate('/DepartmentDashBoard');
      else {
        setError("Unauthorized role.");
        setShowError(true);
      }
    } catch (error) {
      console.error(error);
      setError(error.response?.data?.message || "Invalid username or password.");
      setShowError(true);
    }
  };

  const handleForgotPassword = async () => {
    try {
      await axios.post('http://localhost:8000/api/users/request-reset', { username });
      setStep(2);
    } catch (error) {
      setError(error.response?.data?.message || "User not found.");
      setShowError(true);
    }
  };

  const handleVerifyOtp = async () => {
    try {
      await axios.post('http://localhost:8000/api/users/verify-otp', { username, otp });
      setStep(3);
    } catch (error) {
      setError("Invalid OTP.");
      setShowError(true);
    }
  };

  const handleResetPassword = async () => {
    try {
      await axios.post('http://localhost:8000/api/users/reset-password', { username, newPassword });
      setShowForgotPassword(false);
      setStep(1);
    } catch (error) {
      setError("Password reset failed.");
      setShowError(true);
    }
  };

  return (
    <div className="main">
      <div className="container logmain">
        {!showForgotPassword ? (
          <div className="panel sign-up-panel">
            <h1 className="titleright">Log In to Your Account</h1>
            <form className="signup-form" onSubmit={handleSubmit}>
              {showError && <p className="error-message">{error}</p>}
              <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
              <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
              <button type="submit" className="sign-up-button">LOGIN</button>
            </form>
            <p className="forgot-password" style={{ cursor: 'pointer', color: 'blue', textDecoration: 'underline' }} onClick={() => setShowForgotPassword(true)}>Forgot Password?</p>
          </div>
        ) : (
          <div className="forgot-password-modal">
            <h2>Reset Password</h2>
            {step === 1 && (
              <>
                <input type="text" placeholder="Enter Username" value={username} onChange={(e) => setUsername(e.target.value)} />
                <button onClick={handleForgotPassword}>Request OTP</button>
              </>
            )}
            {step === 2 && (
              <>
                <input type="text" placeholder="Enter OTP" value={otp} onChange={(e) => setOtp(e.target.value)} />
                <button onClick={handleVerifyOtp}>Verify OTP</button>
              </>
            )}
            {step === 3 && (
              <>
                <input type="password" placeholder="New Password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} />
                <button onClick={handleResetPassword}>Reset Password</button>
              </>
            )}
            <button onClick={() => setShowForgotPassword(false)}>Back to Login</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Login;
