import React, { useState, useRef } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { QRCode } from "react-qrcode-logo";
import axios from "axios";
import "./companyAssetTranfer.css";

const CompanyTransfer = () => {
  const location = useLocation();
  const asset = location.state?.asset;
  const user = JSON.parse(localStorage.getItem("currentUser"));

  const [name, setName] = useState(user?.username || "");
  const [company, setCompany] = useState(asset?.company || "");
  const [department, setDepartment] = useState("");
  const [assetUserName, setUserName] = useState("");
  const [assetUpdateDate, setAssetUpdateDate] = useState("");
  const [specialNote, setSpecialNote] = useState("");

  const [qrCodeData, setQrCodeData] = useState([]);
  const navigate = useNavigate();
  const qrCodeContainerRef = useRef(null);

  const departments = ["ICT", "HR", "Kitchen", "Front Office", "Store", "Account", "Audit"];

  const generateTrackingId = (serialNumber) => {
    const companyCodes = {
      Vella: "VE",
      "98 Acers": "98",
      "Ravana Pool Club": "RPC",
      "Flying Ravana": "FR",
      "Le Maas Tota": "LMT",
      "Tea Factory": "TF",
      "Walaa kulu": "WK",
      "kiri kopi": "KK",
    };
    const departmentCodes = {
      ICT: "IT",
      HR: "HR",
      Kitchen: "KT",
      Store: "ST",
      "Front Office": "FO",
      Account: "AC",
      Audit: "AU",
    };

    const companyCode = companyCodes[company] || "XX";
    const departmentCode = departmentCodes[department] || "XX";
    const serialSuffix = serialNumber ? serialNumber.slice(-4) : "";
    const randomNum = new Date().toISOString().slice(2, 10).replace(/-/g, "").slice(0, 6) + 
                      String(Math.floor(Math.random() * 100)).padStart(2, "0");

    return serialNumber ? `${companyCode}-${departmentCode}-${serialSuffix}` : `${companyCode}-${departmentCode}-${randomNum}`;
  };

  const generateQRCode = () => {
    if (!company || !department) {
      alert("Please select both company and department.");
      return;
    }

    const trackingId = generateTrackingId(asset?.serialNumber);
    const qrData = `http://localhost:3000/QRView/${trackingId}`;

    setQrCodeData([{ component: asset?.assetName, qrData, trackingId }]);
  };

  const handleUpdate = async () => {
    if (!company || !department || !assetUserName.trim() || !assetUpdateDate) {
      alert("Please fill all required fields.");
      return;
    }

    const updatedAsset = {
      ...asset,
      company,
      department,
      assetUserName,
      assetUpdateDate,
      specialNote,
      trackingId: qrCodeData[0]?.trackingId,
    };

    try {
      const response = await axios.put(
        `http://localhost:8000/api/AssetRegisterDetails/updateAsset/${asset._id}`,
        updatedAsset
      );
      if (response.status === 200) {
        alert("Asset transferred successfully!");
        navigate("/AssetDetails");
      }
    } catch (error) {
      console.error("Error transferring asset:", error);
      alert("An error occurred while transferring the asset.");
    }
  };

  return (
    <div className="transfer-container">
      <h2 className="trnshead">Transfer Asset</h2>
      <form className="transfer-form">
        <div className="row">
          <div className="form-group col-md-4">
            <label className="l1">Transfer Name:</label>
            <input className="in1" value={name} readOnly />
          </div>
          <div className="form-group col-md-4">
            <label className="l1">Category:</label>
            <input className="in1" value={asset?.mainCategory || ""} readOnly />
          </div>
          <div className="form-group col-md-4">
            <label className="l1">Type:</label>
            <input className="in1" value={asset?.type || ""} readOnly />
          </div>
        </div>

        <div className="row">
          <div className="form-group col-md-4">
            <label className="l1">Asset Name/Brand:</label>
            <input className="in1" value={asset?.assetName || ""} readOnly />
          </div>
          <div className="form-group col-md-4">
            <label className="l1">Serial Number:</label>
            <input className="in1" value={asset?.serialNumber || ""} readOnly />
          </div>
          <div className="form-group col-md-4">
            <label className="l1">Model Number:</label>
            <input className="in1" value={asset?.assetModel || ""} readOnly />
          </div>
        </div>

        <div className="row">
          <div className="form-group col-md-6">
            <label className="l1">Company:</label>
            <input className="in1" value={company} readOnly />
          </div>
          <div className="form-group col-md-6">
            <label className="l1">Department:</label>
            <select className="in2" value={department} onChange={(e) => setDepartment(e.target.value)}>
              <option value="">Select Transfer Department</option>
              {departments.map((dep) => (
                <option key={dep} value={dep}>
                  {dep}
                </option>
              ))}
            </select>
          </div>
        </div>

        <input className="tIn" type="text" value={assetUserName} onChange={(e) => setUserName(e.target.value)} placeholder="User Name" />
        <input className="dat" type="date" value={assetUpdateDate} onChange={(e) => setAssetUpdateDate(e.target.value)} />
        <input className="tIn" type="text" value={specialNote} onChange={(e) => setSpecialNote(e.target.value)} placeholder="Enter Special Note (Optional)" />

        <button type="button" className="button" onClick={generateQRCode}>
          Generate QR
        </button>

        <div ref={qrCodeContainerRef}>
          {qrCodeData.map((item, index) => (
            <div key={index} className="qr-container">
              <QRCode value={item.qrData} size={100} />
              <p className="tid">{item.trackingId}</p>
            </div>
          ))}
        </div>

        {qrCodeData.length > 0 && (
          <button type="button" onClick={handleUpdate} className="transfer-button">
            Transfer
          </button>
        )}
      </form>
    </div>
  );
};

export default CompanyTransfer;
